CEPGP_vSearch = "RAID";
CEPGP_groupVersion = {};
for i = 1, GetNumGroupMembers() do
	local name, _, _, _, class, classFile, _, online = GetRaidRosterInfo(i);
	if online then
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "Addon not enabled",
			[3] = class,
			[4] = classFile
		};
	else
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "Offline",
			[3] = class,
			[4] = classFile
		};
	end
end
CEPGP_groupVersion = CEPGP_tSort(CEPGP_groupVersion, 1);
CEPGP_UpdateVersionScrollBar();

PlaySound(799);
CEPGP_vInfo = {};
CEPGP_vSearch = "GUILD";
CEPGP_SendAddonMsg("version-check", "GUILD");
CEPGP_groupVersion = {};
for i = 1, GetNumGuildMembers() do
	local name, _, _, _, class, _, _, _, online, _, classFileName = GetGuildRosterInfo(i);
	if string.find(name, "-") then
		name = string.sub(name, 0, string.find(name, "-")-1);
	end
	if online then
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "Addon not enabled",
			[3] = class,
			[4] = classFileName
		};
	else
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "Offline",
			[3] = class,
			[4] = classFileName
		};
	end
end
CEPGP_groupVersion = CEPGP_tSort(CEPGP_groupVersion, 1);
CEPGP_UpdateVersionScrollBar();

