else
	STANDBYEP = false;
	CEPGP_options_standby_ep_options:Hide();
end

if self:GetChecked() then
	CEPGP_standby_share = true;
	for name, _ in pairs(CEPGP_standbyRoster) do
		CEPGP_SendAddonMsg("StandbyListAdd;"..name, "RAID");
	end
else
	CEPGP_standby_share = false;
end

