Scripts.OnClick:
if self:GetChecked() then
	CEPGP_context_popup_GP_check:SetChecked(nil);
	CEPGP_context_popup_desc:SetText("Add/Subtract EP");
else
	self:SetChecked(1);
end

if self:GetChecked() then
	CEPGP_context_popup_EP_check:SetChecked(nil);
	CEPGP_context_popup_desc:SetText("Add/Subtract GP");
else
	self:SetChecked(1);
end

PlaySound(799);
local name = CEPGP_guild_log_save_name:GetText();
CEPGP_updateGuild();
if RECORDS[name] == nil or (CEPGP_overwritelog and name == CEPGP_recordholder) then
	RECORDS[name] = {};
	SortGuildRoster(name);
	for i = 1, GetNumGuildMembers(), 1 do
		local _, _, _, _, _, _, _, oNote = GetGuildRosterInfo(i);
		RECORDS[name][GetGuildRosterInfo(i)] = oNote;
	end
	if CEPGP_overwritelog then
		CEPGP_print("Record overwritten [" .. name .. "]");
		CEPGP_overwritelog = false;
	else
		CEPGP_print("Record saved [" .. name .. "]");
	end
	HideUIPanel(CEPGP_save_guild_logs);
else
	CEPGP_print("Record [" .. name .. "] already exists. Click confirm again to overwrite");
	CEPGP_recordholder = name;
	CEPGP_overwritelog = true;
end

Scripts.OnLoad:
self:RegisterForDrag("LeftButton"); CEPGP_guild_log_save_desc:SetText("Name your log

If you choose a name already in use,
it will overwrite the existing log.");

Scripts.OnClick:
local name = UIDropDownMenu_GetSelectedName(CEPGP_restoreDropdown);
CEPGP_updateGuild();
CEPGP_ignoreUpdates = true;
C_Timer.After(0.1, function()
	if RECORDS[name] ~= nil and CEPGP_confirmrestore then
		for i = 1, GetNumGuildMembers(), 1 do
			if RECORDS[name][GetGuildRosterInfo(i)] == nil then
				GuildRosterSetOfficerNote(i, "0,"..BASEGP);
			else
				GuildRosterSetOfficerNote(i, RECORDS[name][GetGuildRosterInfo(i)]);
			end
		end
		CEPGP_print("Guild EPGP standings have been restored from record [" .. name .. "]");
		CEPGP_confirmrestore = false;
		HideUIPanel(CEPGP_restore_guild_logs);
	elseif RECORDS[name] ~= nil then
		CEPGP_print("Are you sure you would like to restore guild EPGP standings from record [" .. name .. "]?");
		CEPGP_print("This action CANNOT be undone. Press confirm again to restore.");
		CEPGP_confirmrestore = true;
	else
		CEPGP_print("Record [" .. name .. "] could not be found", true);
	end
end);
C_Timer.After(1, function()
	CEPGP_ignoreUpdates = true;
	CEPGP_rosterUpdate("GUILD_ROSTER_UPDATE");
end);

local CEPGP_target = CEPGP_settings_import_name:GetText();
if CEPGP_raidRoster[CEPGP_target] then
	CEPGP_SendAddonMsg(CEPGP_target .. ";import", "RAID");
elseif CEPGP_roster[CEPGP_target] then
	CEPGP_SendAddonMsg(CEPGP_target .. ";import", "GUILD");
else
	CEPGP_print(CEPGP_target .. " not found in guild or raid group", true);
end
CEPGP_settings_import_name:ClearFocus();
HideUIPanel(CEPGP_settings_import);

Scripts.OnEnterPressed:
self:ClearFocus();
PlaySound(799);
if CEPGP_override_item_name:GetText() == "" then
	CEPGP_print("Enter an item name", true);
	return;
end
local item = CEPGP_standardiseString(CEPGP_override_item_name:GetText());
if GetItemInfo(item) then
	_, item = GetItemInfo(item); --Converts the item name to a link
end
local gp;
if CEPGP_override_item_gp:GetText() == "" then
	gp = 0;
else
	gp = tonumber(CEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and overwrite) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	CEPGP_print("GP value for " .. item .. " has been overriden to " .. gp);
	CEPGP_UpdateOverrideScrollBar();
	CEPGP_override_confirm = false;
	CEPGP_override_item_name:SetText("");
	CEPGP_override_item_gp:SetText("");
	CEPGP_override_item_name:SetFocus();
else
	CEPGP_print(item .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add again to change self to " .. gp);
	CEPGP_override_confirm = true;
end

Scripts.OnClick:
PlaySound(799);
if CEPGP_override_item_name:GetText() == "" then
		CEPGP_print("Enter an item name", true);
	return;
end
local item = CEPGP_standardiseString(CEPGP_override_item_name:GetText());
local gp;
if CEPGP_override_item_gp:GetText() == "" then
	gp = 0;
else
	gp = tonumber(CEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and CEPGP_override_confirm) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	CEPGP_print("GP value for " .. item .. " has been overriden to " .. gp);
	CEPGP_UpdateOverrideScrollBar();
	CEPGP_override_confirm = false;
	CEPGP_override_item_name:SetText("");
	CEPGP_override_item_name:ClearFocus();
	CEPGP_override_item_gp:SetText("");
	CEPGP_override_item_gp:ClearFocus();
else
	CEPGP_print(item .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add again to change self to " .. gp);
	CEPGP_override_confirm = true;
end

PlaySound(799);
if CEPGP_traffic_clear then
	TRAFFIC = {};
	CEPGP_UpdateTrafficScrollBar();
	CEPGP_print("EPGP traffic wiped");
	CEPGP_traffic_clear = false;
else
	CEPGP_print("You are trying to clear your EPGP traffic log. This action cannot be reversed.");
	CEPGP_print("Press Clear EPGP traffic again to confirm");
	CEPGP_traffic_clear = true;
end

PlaySound(799);
if UIDropDownMenu_GetSelectedValue(CEPGP_attendance_dropdown) == 0 then
	CEPGP_snapshot = nil;
	CEPGP_attendance_desc:SetText("Snapshot Summary");
else
	CEPGP_snapshot = UIDropDownMenu_GetSelectedValue(CEPGP_attendance_dropdown);
	CEPGP_attendance_desc:SetText("Viewing Snapshot: " .. date("%A %d %B, %Y, %H:%M", CEPGP_raid_logs[CEPGP_snapshot][1]));
end
CEPGP_UpdateAttendanceScrollBar();

