Scripts.OnShow:
local temp = {};
local dump = "";
local size = CEPGP_ntgetn(CEPGP_roster);
for k, v in pairs(CEPGP_roster) do
	temp[CEPGP_ntgetn(temp)+1] = {
		[1] = k,
		[2] = v[2],
		[3] = v[3],
		[4] = v[4],
		[5] = v[5],
		[6] = v[6]
	};
end
temp = CEPGP_tSort(temp, 1);
for i = 1, size do
	dump = dump .. temp[i][1] .. "," .. temp[i][2] .. "," .. temp[i][3] .. "," .. temp[i][5] .. "," .. temp[i][6] .. "\n"; --Line 16
end
self:SetText(dump);
self:HighlightText();
temp = nil;
dump = nil;
size = nil;
